import { Injectable } from '@angular/core';
import { Product } from './products';

@Injectable({
  providedIn: 'root'
})
export class CartService {
  products: Product[] = [];

  constructor() { }

  addToCart(product: Product) {
    this.products.push(product);
  }

  getItems(){
    return this.products;
  }

  cleanCart(){
    this.products = [];
    return this.products;
  }
}
